import React, { useState } from "react";

const emojis = ["😀", "😂", "😍", "🤔", "🙄", "😴"];

function App() {
	const [currentEmoji, setCurrentEmoji] = useState(emojis[0]);
	const [description, setDescription] = useState("");

	const handleNewEmoji = () => {
		const randomIndex = Math.floor(Math.random() * emojis.length);
		setCurrentEmoji(emojis[randomIndex]);
		setDescription("");
	};

	const handleInputChange = (event) => {
		setDescription(event.target.value);
	};

	return (
		<div>
			<h1>Random Emoji Description</h1>
			<div>
				<span style={{ fontSize: "3rem" }}>{currentEmoji}</span>
				<button onClick={handleNewEmoji}>New Emoji</button>
			</div>
			<input
				type="text"
				value={description}
				onChange={handleInputChange}
				placeholder="Describe the emoji"
			/>
			<p>{description}</p>
		</div>
	);
}

export default App;
